import React, { useState } from 'react';
import '../styles/SideNav.css';
import { FiGrid, FiSearch, FiImage, FiCpu, FiLayers, FiBarChart2, FiSettings, FiChevronLeft } from 'react-icons/fi';

function SideNav() {
  const [isExpanded, setIsExpanded] = useState(false);

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  };

  const navItems = [
    { id: 'hero', icon: <FiGrid />, label: 'Home', section: 'hero-section' },
    { id: 'comparison', icon: <FiBarChart2 />, label: 'Comparison', section: 'comparison-section' },
    { id: 'usecases', icon: <FiLayers />, label: 'Use Cases', section: 'usecases-section' },
    { id: 'architecture', icon: <FiCpu />, label: 'Architecture', section: 'architecture-section' },
    { id: 'process', icon: <FiSettings />, label: 'Process', section: 'process-section' },
    { id: 'search', icon: <FiSearch />, label: 'Search', section: 'search-section' },
    { id: 'results', icon: <FiImage />, label: 'Results', section: 'results-section' },
  ];

  return (
    <>
      {/* Trigger Box - Always visible on right side */}
      <div 
        className={`sidenav-trigger ${isExpanded ? 'active' : ''}`}
        onMouseEnter={() => setIsExpanded(true)}
      >
        <div className="trigger-icon">
          <FiChevronLeft className={`chevron ${isExpanded ? 'rotated' : ''}`} />
        </div>
        <div className="trigger-dots">
          <span></span>
          <span></span>
          <span></span>
        </div>
      </div>

      {/* Navigation Panel */}
      <nav 
        className={`sidenav ${isExpanded ? 'expanded' : ''}`}
        onMouseEnter={() => setIsExpanded(true)}
        onMouseLeave={() => setIsExpanded(false)}
      >
        <div className="sidenav-header">
          <span className="sidenav-title">Navigate</span>
        </div>
        
        <ul className="sidenav-menu">
          {navItems.map((item) => (
            <li key={item.id} className="sidenav-item">
              <button 
                onClick={() => scrollToSection(item.section)}
                className="sidenav-link"
              >
                <span className="sidenav-icon">{item.icon}</span>
                <span className="sidenav-label">{item.label}</span>
              </button>
            </li>
          ))}
        </ul>

        <div className="sidenav-footer">
          <span className="sidenav-hint">Hover to navigate</span>
        </div>
      </nav>

      {/* Overlay when expanded */}
      <div 
        className={`sidenav-overlay ${isExpanded ? 'visible' : ''}`}
        onClick={() => setIsExpanded(false)}
      />
    </>
  );
}

export default SideNav;
