import React, { useState, useEffect } from 'react';
import './App.css';
import ProcessPanel from './components/ProcessPanel';
import SearchPanel from './components/SearchPanel';
import ResultsGrid from './components/ResultsGrid';
import { FiSearch, FiImage } from 'react-icons/fi';

function App() {
  const [activeTab, setActiveTab] = useState('process');
  const [metadata, setMetadata] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [searchResults, setSearchResults] = useState([]);

  const handleProcessComplete = (newMetadata) => {
    setMetadata(newMetadata);
    setSearchResults([]);
    setError(null);
    // Auto-switch to search tab after processing
    setTimeout(() => setActiveTab('search'), 500);
  };

  const handleLoadMetadata = (loadedMetadata) => {
    setMetadata(loadedMetadata);
    setSearchResults([]);
    setError(null);
    // Auto-switch to search tab after loading
    setTimeout(() => setActiveTab('search'), 500);
  };

  const handleSearch = (results) => {
    setSearchResults(results);
  };

  const handleError = (errorMsg) => {
    setError(errorMsg);
  };

  return (
    <div className="App">
      <header className="app-header">
        <div className="header-content">
          <h1>ðŸŽ¯ YOLOv11 Image Search</h1>
          <p>Computer Vision Powered Object Detection & Search</p>
        </div>
      </header>

      <div className="container">
        <nav className="tab-navigation">
          <button
            className={`tab-button ${activeTab === 'process' ? 'active' : ''}`}
            onClick={() => setActiveTab('process')}
          >
            <FiImage /> Process Images
          </button>
          <button
            className={`tab-button ${activeTab === 'search' ? 'active' : ''}`}
            onClick={() => setActiveTab('search')}
            disabled={!metadata}
          >
            <FiSearch /> Search
          </button>
        </nav>

        {error && (
          <div className="error-banner">
            <p>{error}</p>
            <button onClick={() => setError(null)}>Ã—</button>
          </div>
        )}

        <main className="main-content">
          {activeTab === 'process' && (
            <ProcessPanel
              onProcessComplete={handleProcessComplete}
              onLoadMetadata={handleLoadMetadata}
              onError={handleError}
            />
          )}

          {activeTab === 'search' && metadata && (
            <SearchPanel
              metadata={metadata}
              onSearch={handleSearch}
              onError={handleError}
            />
          )}
        </main>

        {searchResults.length > 0 && (
          <ResultsGrid
            results={searchResults}
            metadata={metadata}
          />
        )}

        {metadata && !searchResults.length && activeTab === 'search' && (
          <div className="empty-state">
            <h3>Ready to search</h3>
            <p>Select classes and configure search parameters to find matching images</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
