import React, { useState, useEffect } from 'react';
import '../styles/SideNav.css';
import { FiGrid, FiSearch, FiImage, FiCpu, FiLayers, FiBarChart2, FiSettings, FiChevronLeft, FiZap } from 'react-icons/fi';

function SideNav() {
  const [isExpanded, setIsExpanded] = useState(false);
  const [activeSection, setActiveSection] = useState('hero');

  // Track active section on scroll
  useEffect(() => {
    const handleScroll = () => {
      const sections = [
        'hero-section',
        'comparison-section', 
        'usecases-section',
        'architecture-section',
        'process-section',
        'search-section',
        'results-section'
      ];
      
      for (const sectionId of sections) {
        const element = document.getElementById(sectionId);
        if (element) {
          const rect = element.getBoundingClientRect();
          if (rect.top <= 200 && rect.bottom >= 200) {
            setActiveSection(sectionId.replace('-section', ''));
            break;
          }
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToSection = (sectionId, itemId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth', block: 'start' });
      setActiveSection(itemId);
    }
  };

  const navItems = [
    { id: 'hero', icon: <FiZap />, label: 'Home', section: 'hero-section' },
    { id: 'comparison', icon: <FiBarChart2 />, label: 'Compare', section: 'comparison-section' },
    { id: 'usecases', icon: <FiLayers />, label: 'Use Cases', section: 'usecases-section' },
    { id: 'architecture', icon: <FiCpu />, label: 'Architecture', section: 'architecture-section' },
    { id: 'process', icon: <FiSettings />, label: 'Process', section: 'process-section' },
    { id: 'search', icon: <FiSearch />, label: 'Search', section: 'search-section' },
    { id: 'results', icon: <FiImage />, label: 'Results', section: 'results-section' },
  ];

  return (
    <>
      {/* Trigger Box - Always visible on right side */}
      <div 
        className={`sidenav-trigger ${isExpanded ? 'hidden' : ''}`}
        onMouseEnter={() => setIsExpanded(true)}
      >
        <div className="trigger-line"></div>
        <div className="trigger-glow"></div>
      </div>

      {/* Navigation Panel */}
      <nav 
        className={`sidenav ${isExpanded ? 'expanded' : ''}`}
        onMouseEnter={() => setIsExpanded(true)}
        onMouseLeave={() => setIsExpanded(false)}
      >
        {/* Decorative top accent */}
        <div className="sidenav-accent"></div>
        
        <ul className="sidenav-menu">
          {navItems.map((item, index) => (
            <li key={item.id} className="sidenav-item" style={{ '--delay': `${index * 0.05}s` }}>
              <button 
                onClick={() => scrollToSection(item.section, item.id)}
                className={`sidenav-link ${activeSection === item.id ? 'active' : ''}`}
              >
                <span className="sidenav-icon">{item.icon}</span>
                <span className="sidenav-label">{item.label}</span>
                <span className="sidenav-indicator"></span>
              </button>
            </li>
          ))}
        </ul>

        {/* Decorative bottom element */}
        <div className="sidenav-decoration">
          <div className="deco-orb"></div>
        </div>
      </nav>
    </>
  );
}

export default SideNav;
