import React, { useState, useEffect } from 'react';
import '../styles/ResultsGrid.css';

function ResultsGrid({ results, metadata }) {
  const [gridColumns, setGridColumns] = useState(3);
  const [showBoxes, setShowBoxes] = useState(true);
  const [highlightMatches, setHighlightMatches] = useState(true);
  const [debugMode, setDebugMode] = useState(false);

  const handleDownload = () => {
    const dataStr = JSON.stringify(results, null, 2);
    const dataBlob = new Blob([dataStr], { type: 'application/json' });
    const url = URL.createObjectURL(dataBlob);
    const link = document.createElement('a');
    link.href = url;
    link.download = 'search_results.json';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  return (
    <div className="results-section">
      <div className="results-header">
        <h2>üì∑ Results: {results.length} matching images</h2>

        <div className="display-controls">
          <div className="control-group">
            <label>
              <input
                type="checkbox"
                checked={showBoxes}
                onChange={(e) => setShowBoxes(e.target.checked)}
              />
              Show bounding boxes
            </label>
          </div>

          <div className="control-group">
            <label>Columns:</label>
            <input
              type="range"
              min="2"
              max="6"
              value={gridColumns}
              onChange={(e) => setGridColumns(parseInt(e.target.value))}
            />
            <span>{gridColumns}</span>
          </div>

          <div className="control-group">
            <label>
              <input
                type="checkbox"
                checked={highlightMatches}
                onChange={(e) => setHighlightMatches(e.target.checked)}
              />
              Highlight matching classes
            </label>
          </div>

          <div className="control-group">
            <label>
              <input
                type="checkbox"
                checked={debugMode}
                onChange={(e) => setDebugMode(e.target.checked)}
              />
              üêõ Debug
            </label>
          </div>

          <button className="btn btn-download" onClick={handleDownload}>
            ‚¨áÔ∏è Download Results
          </button>
        </div>
      </div>

      {debugMode && results.length > 0 && (
        <div style={{ background: '#f0f0f0', padding: '10px', marginBottom: '10px', fontSize: '11px', maxHeight: '150px', overflow: 'auto', borderRadius: '4px', border: '1px solid #ccc' }}>
          <strong>üêõ Debug Info:</strong>
          <pre style={{ margin: '5px 0', whiteSpace: 'pre-wrap' }}>
{JSON.stringify({
  firstResult: results[0],
  imagePathField: results[0]?.image_path,
  expected_url: `/api/image?imagePath=${encodeURIComponent(results[0]?.image_path || '')}`
}, null, 2)}
          </pre>
        </div>
      )}

      <div className="results-grid" style={{ gridTemplateColumns: `repeat(${gridColumns}, 1fr)` }}>
        {results.map((result, idx) => (
          <ImageCard
            key={idx}
            result={result}
            showBoxes={showBoxes}
            highlightMatches={highlightMatches}
            debugMode={debugMode}
          />
        ))}
      </div>
    </div>
  );
}

function ImageCard({ result, showBoxes, highlightMatches, debugMode }) {
  const [imageLoaded, setImageLoaded] = useState(false);
  const [imageUrl, setImageUrl] = useState('');
  const [loadError, setLoadError] = useState(null);
  const canvasRef = React.useRef(null);

  useEffect(() => {
    // Convert file path to API URL
    // The backend will handle both absolute and relative paths
    const imagePath = result.image_path;
    console.log('üì∑ ImageCard received path:', imagePath);
    
    if (!imagePath) {
      console.error('‚ùå No image_path in result:', result);
      setLoadError('No image path');
      return;
    }
    
    // Create the API endpoint URL with proper encoding
    const encodedPath = encodeURIComponent(imagePath);
    const url = `/api/image?imagePath=${encodedPath}`;
    console.log('üîó API URL:', url);
    
    setImageUrl(url);
    setLoadError(null);
    setImageLoaded(false);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [result.image_path]);

  // Draw boxes on canvas after image loads
  useEffect(() => {
    if (!showBoxes || !imageLoaded || !imageUrl || !canvasRef.current) {
      return;
    }

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const img = new Image();
    
    img.onload = () => {
      console.log('‚úÖ Canvas image loaded, drawing detections');
      canvas.width = img.width;
      canvas.height = img.height;
      ctx.drawImage(img, 0, 0);

      result.detections.forEach((det) => {
        const [x1, y1, x2, y2] = det.bbox;
        const color = '#30C938';

        ctx.strokeStyle = color;
        ctx.lineWidth = 3;
        ctx.strokeRect(x1, y1, x2 - x1, y2 - y1);

        const label = `${det.class} ${det.confidence.toFixed(2)}`;
        ctx.fillStyle = color;
        ctx.font = 'bold 12px Arial';
        const textMetrics = ctx.measureText(label);
        ctx.fillRect(x1, y1 - 20, textMetrics.width + 8, 20);

        ctx.fillStyle = 'white';
        ctx.fillText(label, x1 + 4, y1 - 5);
      });
    };
    
    img.onerror = (error) => {
      console.error('‚ùå Canvas image failed to load:', imageUrl, error);
      setLoadError('Failed to load image for canvas');
    };
    
    console.log('üé® Loading image for canvas:', imageUrl);
    img.src = imageUrl;
  }, [showBoxes, imageLoaded, imageUrl, result.detections]);

  const renderImage = () => {
    if (showBoxes && imageUrl) {
      return (
        <canvas
          ref={canvasRef}
          style={{ width: '100%', height: 'auto' }}
        />
      );
    }

    if (!imageUrl) {
      return <div style={{ background: '#eee', width: '100%', height: '200px' }}>Loading...</div>;
    }

    return (
      <img
        src={imageUrl}
        alt="Result"
        style={{ width: '100%', height: 'auto', objectFit: 'cover', minHeight: '200px' }}
        onLoad={() => {
          console.log('‚úÖ Image loaded:', imageUrl);
          setImageLoaded(true);
          setLoadError(null);
        }}
        onError={(error) => {
          console.error('‚ùå Image failed to load:', imageUrl, error);
          setLoadError('Failed to load image');
        }}
      />
    );
  };

  const metaItems = Object.entries(result.class_counts)
    .map(([cls, count]) => `${cls}: ${count}`)
    .join(', ');

  return (
    <div className="image-card">
      <div className="image-container">
        {loadError ? (
          <div style={{ 
            background: '#ffcccc', 
            width: '100%', 
            height: '200px', 
            display: 'flex', 
            alignItems: 'center',
            justifyContent: 'center',
            color: '#cc0000',
            fontSize: '12px',
            padding: '10px',
            boxSizing: 'border-box'
          }}>
            {loadError}
          </div>
        ) : (
          renderImage()
        )}
      </div>
      <div className="meta-overlay">
        <strong>{result.image_path.split('\\').pop() || result.image_path.split('/').pop()}</strong>
        <br />
        <small>{metaItems || 'No matches'}</small>
      </div>
    </div>
  );
}

export default ResultsGrid;
